import React, { Component } from "react";

// import all the necessary subcomponents
import Loader from '../Loader';

// importing the style from the external css file
import "./exchange.css";


// declaring the type of states and props used
type Props = {};
type State = {
  cryptoCurrencyList: Array<Object>,

};

class Exchange extends Component<Props, State> {
  constructor(props) {
    super(props);
    this.state = {
      cryptoCurrencyList: [],
    };

    //  binding all the necessary functions to perform state operations
    (this: any).handleSearchTypeChange = this.handleSearchTypeChange.bind(this);
  }
  componentDidMount() {
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const url = "https://api.coinswitch.co/v2/coins";
    fetch(proxyurl + url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'x-api-key': 't41E6v16mG6xqOUK74E2F7Py6UVng4K6n1pO3Jig',
          'x-user-ip': '1.1.1.1',
        },
        })
        .then(response => response.json())
        .then(data_list => this.setState(prevState=>({
          cryptoCurrencyList: data_list.data,
          }))
        );
      const url1 = "https://api.coinswitch.co/v2/rate";
      fetch(proxyurl + url1, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'x-api-key': 't41E6v16mG6xqOUK74E2F7Py6UVng4K6n1pO3Jig',
            'x-user-ip': '1.1.1.1',
          },
          body: {depositCoin: 'btc', destinationCoin: 'eth'},
          json: true,
          })
          .then(response => response.json())
          .then(data_list => {
            console.log('data_list###', data_list);
          }
          );
  }

  // handling on change for select box in the search area (first name, last name, email)
  handleSearchTypeChange(e) {
    console.log('koko');
  }

  render() {
    console.log('cryptoCurrencyList', this.state.cryptoCurrencyList);
    return (
      <div>
        <div>
          Exchange
        </div>
      </div>
      );
  }
}

export default Exchange;
