// @flow

import React, { Component } from "react";

// import all the necessary subcomponents
import Loader from '../Loader';
import CircularProgress from '@material-ui/core/CircularProgress';

// importing the style from the external css file
import "./inputbox.css";


// declaring the type of states and props used
type Props = {};
type State = {
  cryptoCurrencyList: Array<Object>,

};

class InputBox extends Component<Props, State> {
  constructor(props) {
    super(props);
    this.state = {
      showDepositCurrency: false,
      search: '',
    };

    //  binding all the necessary functions to perform state operations
    (this: any).handleSearchTypeChange = this.handleSearchTypeChange.bind(this);
    (this: any).onInputChange = this.onInputChange.bind(this);
    (this: any).onClickDepositCurrency = this.onClickDepositCurrency.bind(this);
  }
  componentDidMount() {
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const url = "https://api.coinswitch.co/v2/coins";
    fetch(proxyurl + url, {
        method: 'GET',
        headers: {
          'Access-Control-Allow-Origin': '*',
          'Content-Type': 'application/json',
          'x-api-key': 't41E6v16mG6xqOUK74E2F7Py6UVng4K6n1pO3Jig',
          'x-user-ip': '1.1.1.1',
        },
        })
        .then(response => response.json())
        .then(data_list => this.setState(prevState=>({
          cryptoCurrencyList: data_list.data.sort(currency=> currency.isActive),
          depositCoin: data_list.data.sort(currency=> currency.isActive)[0],
          destinationCoin: data_list.data.sort(currency=> currency.isActive)[1],
          }))
        );

      // ----------------------------------------------------

      // const url1 = "https://api.coinswitch.co/v2/rate";
      // var data = JSON.stringify({
      //   "depositCoin": "btc",
      //   "destinationCoin": "eth"
      // });
      // fetch(proxyurl + url1, {
      //     method: 'POST',
      //     headers: {
      //       'Content-Type': 'application/json',
      //       'x-api-key': 't41E6v16mG6xqOUK74E2F7Py6UVng4K6n1pO3Jig',
      //       'x-user-ip': '1.1.1.1',
      //     },
      //     body: data,
      //     json: true,
      //     })
      //     .then(response => response.json())
      //     .then(data_list => {
      //       console.log('data_list###', data_list);
      //     }
      //     );
  }

  // handling on change for select box in the search area (first name, last name, email)
  handleSearchTypeChange(e) {
    console.log('koko');
  }
  onInputChange(e) {
    const { name, value } = e.target;
    if (name === 'depositValue' && value) {
      this.props.depositValueChange(value);
      this.props.onInputChange(this.props.name, value)
    }
    // this.setState(prevState=>({
    //   [name]: value,
    //   }))
  }
  onClickDepositCurrency() {
    const showDepositCurrency = !this.state.showDepositCurrency;
    this.setState(prevState=>({
      showDepositCurrency,
      search: '',
    }));
  }

  render() {
    return (
      <div className="input-container">
        <input
          name="depositValue"
          type="number"
          className="currency-input"
          value={this.props.value}
          disabled={this.props.disabled}
          step="any"
          onChange={this.onInputChange}
        />
        {this.props.dataInProgress ?
          <div className="loader">
            <CircularProgress />
          </div> :
          null
        }
        <div className="deposit-currency-value" onClick={this.onClickDepositCurrency}>
          <img className="crypto-currency-logo" alt="logo" src="https://files.coinswitch.co/public/coins/eur.jpeg" />
          <div>{this.props.symbol}</div>
          <i className="material-icons" style={{marginLeft: 5}}>keyboard_arrow_down</i>
        </div>
          <div style={{height:this.state.showDepositCurrency ? 300 : 0}} className="crypto-currency-list">
            <div className="search-box">
              <input
                type="text"
                name="search"
                className="currency-search"
                placeholder="search from 434 coins"
                value={this.state.search}
                step="any"
                onChange={this.onInputChange}
              />
              <i  onClick={this.onClickDepositCurrency} className="material-icons currency-search-icon">search</i>
              <i  onClick={this.onClickDepositCurrency} className="material-icons currency-clear">close</i>
            </div>
            {this.props.cryptoCurrencyList && this.props.cryptoCurrencyList.length ?
              this.props.cryptoCurrencyList.filter((item) =>
                (item.name && item.name.toLowerCase().search(this.state.search.toLowerCase()) !== -1) ||
                (item.symbol && item.symbol.toLowerCase().search(this.state.search.toLowerCase()) !== -1)
              ).map((item, index) => (
                <div className="crypto-currency-item" key={item.name + index} >
                  <img className="crypto-currency-logo" alt="logo" src="https://files.coinswitch.co/public/coins/eur.jpeg" />
                  <div className="crypto-currency-symbol" >{item.symbol}</div>
                  <div className="crypto-currency-name" >{item.name}</div>
                </div>
                ))
                : null
            }
          </div>
      </div>
      );
  }
}

export default InputBox;
