// @flow

import React, { Component } from "react";

// import all the necessary subcomponents
import Loader from '../Loader';

// importing the style from the external css file
import "./exchange.css";


// declaring the type of states and props used
type Props = {};
type State = {
  cryptoCurrencyList: Array<Object>,

};

class Exchange extends Component<Props, State> {
  constructor(props) {
    super(props);
    this.state = {
      cryptoCurrencyList: [],
      depositValue: 0,
      destinationValue: 0,
      depositCoin: '',
      destinationCoin: '',
    };

    //  binding all the necessary functions to perform state operations
    (this: any).handleSearchTypeChange = this.handleSearchTypeChange.bind(this);
    (this: any).onInputChange = this.onInputChange.bind(this);
  }
  componentDidMount() {
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const url = "https://api.coinswitch.co/v2/coins";
    fetch(proxyurl + url, {
        method: 'GET',
        headers: {
          'Access-Control-Allow-Origin': '*',
          'Content-Type': 'application/json',
          'x-api-key': 't41E6v16mG6xqOUK74E2F7Py6UVng4K6n1pO3Jig',
          'x-user-ip': '1.1.1.1',
        },
        })
        .then(response => response.json())
        .then(data_list => this.setState(prevState=>({
          cryptoCurrencyList: data_list.data.sort(currency=> currency.isActive),
          }))
        );

      // ----------------------------------------------------

      // const url1 = "https://api.coinswitch.co/v2/rate";
      // var data = JSON.stringify({
      //   "depositCoin": "btc",
      //   "destinationCoin": "eth"
      // });
      // fetch(proxyurl + url1, {
      //     method: 'POST',
      //     headers: {
      //       'Content-Type': 'application/json',
      //       'x-api-key': 't41E6v16mG6xqOUK74E2F7Py6UVng4K6n1pO3Jig',
      //       'x-user-ip': '1.1.1.1',
      //     },
      //     body: data,
      //     json: true,
      //     })
      //     .then(response => response.json())
      //     .then(data_list => {
      //       console.log('data_list###', data_list);
      //     }
      //     );
  }

  // handling on change for select box in the search area (first name, last name, email)
  handleSearchTypeChange(e) {
    console.log('koko');
  }
  onInputChange(e) {
    const { name, value } = e.target;
    console.log('name, value', name, value);
    this.setState(prevState=>({
      [name]: value,
      }))
  }

  render() {
    // console.log('cryptoCurrencyList', this.state.cryptoCurrencyList);
    return (
      <div style={{width: '100%', height: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
        <div className="exchange-container">
          <div>
            <input
              name="depositValue"
              type="number"
              value={this.state.depositValue}
              step="any"
              onChange={this.onInputChange}
            />
            <div>
            {this.state.cryptoCurrencyList && this.state.cryptoCurrencyList.length ?
              this.state.cryptoCurrencyList.map(item => (
                <option>
                  <div style={{display: 'flex'}}>
                    <img alt="logo" src={item.logoUrl} />
                    <div>{item.symbol}</div>
                    <div>{item.name}</div>
                  </div>
                </option>
                ))
                : null
            }
            </div>
          </div>
        </div>
        {this.state.cryptoCurrencyList && this.state.cryptoCurrencyList.length ?
          this.state.cryptoCurrencyList.map(item => (
            <div>
              <img alt="logo" src={item.logoUrl} />
              <div>{item.symbol}</div>
              <div>{item.name}</div>
            </div>
            ))
            : null
        }
      </div>
      );
  }
}

export default Exchange;
